import React from 'react';
import { motion } from 'framer-motion';
import Navbar from '../../components/Navbar';
import Footer from '../../components/Footer';
import { useLanguage } from '../../context/LanguageContext';
import './WorksPage.css';

const photos = [
    {
        id: 1,
        image: "/images/works/bmw-m4.jpg",
        size: "large"
    },
    {
        id: 2,
        image: "/images/works/mercedes-amg.jpg",
        size: "small"
    },
    {
        id: 3,
        image: "/images/works/porsche-gt3.jpg",
        size: "large"
    },
    {
        id: 4,
        image: "/images/works/audi-rs6.jpg",
        size: "medium"
    },
    {
        id: 5,
        image: "/images/works/bmw-m4.jpg",
        size: "small"
    },
    {
        id: 6,
        image: "/images/works/mercedes-amg.jpg",
        size: "large"
    },
    {
        id: 7,
        image: "/images/works/porsche-gt3.jpg",
        size: "medium"
    },
    {
        id: 8,
        image: "/images/works/audi-rs6.jpg",
        size: "small"
    }
];

function WorksPage() {
    const { t } = useLanguage();

    return (
        <div className="works-page">
            <Navbar />

            <main className="relative z-10 pt-[60px]">
                <section className="works-hero pattern-grid pattern-grid-blue">
                    <div className="container mx-auto px-4 py-16 sm:py-24">
                        <motion.h1
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ duration: 0.5 }}
                            className="text-4xl sm:text-5xl lg:text-6xl font-bold text-center mb-6"
                        >
                            {t('works')}
                        </motion.h1>
                        <motion.p
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ duration: 0.5, delay: 0.2 }}
                            className="text-lg sm:text-xl text-gray-300 text-center max-w-3xl mx-auto"
                        >
                            {t('worksSubtitle')}
                        </motion.p>
                    </div>
                </section>

                <section className="photo-gallery">
                    <div className="masonry-grid">
                        {photos.map((photo) => (
                            <motion.div
                                key={photo.id}
                                initial={{ opacity: 0, y: 20 }}
                                animate={{ opacity: 1, y: 0 }}
                                transition={{ duration: 0.5 }}
                                className={`masonry-item ${photo.size}`}
                            >
                                <div className="photo-card">
                                    <img
                                        src={photo.image}
                                        alt=""
                                        className="photo"
                                        loading="lazy"
                                    />
                                </div>
                            </motion.div>
                        ))}
                    </div>
                </section>
            </main>

            <Footer />
        </div>
    );
}

export default WorksPage;
