import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import Navbar from '../../components/Navbar';
import Footer from '../../components/Footer';
import { useLanguage } from '../../context/LanguageContext';
import './WorksPage.css';

const works = [
    {
        id: 1,
        title: "BMW M4 Competition",
        category: "ceramicCoating",
        image: "/images/works/bmw-m4.jpg",
    },
    {
        id: 2,
        title: "Mercedes-AMG GT",
        category: "paintProtection",
        image: "/images/works/mercedes-amg.jpg",
    },
    {
        id: 3,
        title: "Porsche 911 GT3",
        category: "paintCorrection",
        image: "/images/works/porsche-gt3.jpg",
    },
    {
        id: 4,
        title: "Audi RS6",
        category: "detailedCleaning",
        image: "/images/works/audi-rs6.jpg",
    }
];

const categories = [
    { id: 'all', name: 'allWorks' },
    { id: 'ceramicCoating', name: 'ceramicCoating' },
    { id: 'paintProtection', name: 'paintProtection' },
    { id: 'paintCorrection', name: 'paintCorrection' },
    { id: 'detailedCleaning', name: 'detailedCleaning' }
];

const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
        opacity: 1,
        transition: {
            staggerChildren: 0.1
        }
    }
};

const itemVariants = {
    hidden: { opacity: 0 },
    visible: {
        opacity: 1,
        transition: {
            duration: 0.5
        }
    },
    exit: {
        opacity: 0,
        transition: {
            duration: 0.3
        }
    }
};

function WorksPage() {
    const [selectedCategory, setSelectedCategory] = useState('all');
    const { t } = useLanguage();

    const filteredWorks = works.filter(work => 
        selectedCategory === 'all' || work.category === selectedCategory
    );

    return (
        <div className="works-page">
            <Navbar />

            <main className="pt-[72px]">
                <section className="gallery-hero pattern-grid pattern-grid-blue">
                    <div className="container mx-auto px-4 py-24 sm:py-32">
                        <motion.h1 
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ duration: 0.5 }}
                            className="text-4xl sm:text-5xl lg:text-6xl font-bold text-center mb-6"
                        >
                            {t('worksTitle')}
                        </motion.h1>
                        <motion.p 
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ duration: 0.5, delay: 0.2 }}
                            className="text-lg sm:text-xl text-gray-300 text-center max-w-3xl mx-auto"
                        >
                            {t('worksSubtitle')}
                        </motion.p>
                    </div>
                </section>

                <section className="gallery-section">
                    <div className="container mx-auto px-4 py-16">
                        <div className="filter-buttons">
                            {categories.map(category => (
                                <button
                                    key={category.id}
                                    onClick={() => setSelectedCategory(category.id)}
                                    className={`filter-btn ${selectedCategory === category.id ? 'active' : ''}`}
                                >
                                    {t(category.name)}
                                </button>
                            ))}
                        </div>

                        <motion.div 
                            className="photo-grid"
                            variants={containerVariants}
                            initial="hidden"
                            animate="visible"
                        >
                            <AnimatePresence mode="wait">
                                {filteredWorks.map(work => (
                                    <motion.div
                                        key={work.id}
                                        variants={itemVariants}
                                        layout
                                        className="photo-item"
                                    >
                                        <img 
                                            src={work.image} 
                                            alt={work.title}
                                            className="photo-image"
                                        />
                                    </motion.div>
                                ))}
                            </AnimatePresence>
                        </motion.div>
                    </div>
                </section>
            </main>

            <Footer />
        </div>
    );
}

export default WorksPage;
