import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import Navbar from '../../components/Navbar';
import Footer from '../../components/Footer';
import { useLanguage } from '../../context/LanguageContext';
import './WorksPage.css';

const works = [
    {
        id: 1,
        title: "BMW M4 Competition",
        category: "ceramicCoating",
        image: "/images/works/bmw-m4.jpg",
        description: "ceramicDesc",
        date: "2023-12-15",
        size: "large"
    },
    {
        id: 2,
        title: "Mercedes-AMG GT",
        category: "paintProtection",
        image: "/images/works/mercedes-amg.jpg",
        description: "paintDesc",
        date: "2023-12-10",
        size: "small"
    },
    {
        id: 3,
        title: "Porsche 911 GT3",
        category: "paintCorrection",
        image: "/images/works/porsche-gt3.jpg",
        description: "paintDesc",
        date: "2023-12-05",
        size: "small"
    },
    {
        id: 4,
        title: "Audi RS6",
        category: "detailedCleaning",
        image: "/images/works/audi-rs6.jpg",
        description: "interiorDesc",
        date: "2023-12-01",
        size: "large"
    }
];

const categories = [
    { id: 'all', name: 'allWorks' },
    { id: 'ceramicCoating', name: 'ceramicCoating' },
    { id: 'paintProtection', name: 'paintProtection' },
    { id: 'paintCorrection', name: 'paintCorrection' },
    { id: 'detailedCleaning', name: 'detailedCleaning' }
];

const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
        opacity: 1,
        transition: {
            staggerChildren: 0.1
        }
    }
};

const itemVariants = {
    hidden: { opacity: 0, scale: 0.95 },
    visible: {
        opacity: 1,
        scale: 1,
        transition: {
            duration: 0.5
        }
    },
    exit: {
        opacity: 0,
        scale: 0.95,
        transition: {
            duration: 0.3
        }
    }
};

function WorksPage() {
    const [selectedCategory, setSelectedCategory] = useState('all');
    const { t } = useLanguage();

    const filteredWorks = works.filter(work => 
        selectedCategory === 'all' || work.category === selectedCategory
    );

    return (
        <div className="works-page">
            <Navbar />

            <main className="pt-[72px]">
                <section className="gallery-hero">
                    <div className="hero-background">
                        <div className="gradient-overlay"></div>
                        <div className="pattern-overlay"></div>
                    </div>
                    <div className="container mx-auto px-4 py-24 sm:py-32">
                        <motion.h1 
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ duration: 0.5 }}
                            className="text-4xl sm:text-5xl lg:text-6xl font-bold text-center mb-6"
                        >
                            {t('worksTitle')}
                        </motion.h1>
                        <motion.p 
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ duration: 0.5, delay: 0.2 }}
                            className="text-lg sm:text-xl text-gray-300 text-center max-w-3xl mx-auto"
                        >
                            {t('worksSubtitle')}
                        </motion.p>
                    </div>
                </section>

                <section className="gallery-section">
                    <div className="container mx-auto px-4 py-16">
                        <div className="category-filters">
                            {categories.map(category => (
                                <button
                                    key={category.id}
                                    onClick={() => setSelectedCategory(category.id)}
                                    className={`category-btn ${selectedCategory === category.id ? 'active' : ''}`}
                                >
                                    {t(category.name)}
                                </button>
                            ))}
                        </div>

                        <motion.div 
                            className="works-grid"
                            variants={containerVariants}
                            initial="hidden"
                            animate="visible"
                        >
                            <AnimatePresence mode="wait">
                                {filteredWorks.map(work => (
                                    <motion.div
                                        key={work.id}
                                        layout
                                        variants={itemVariants}
                                        className={`work-card ${work.size}`}
                                    >
                                        <div className="work-card-inner">
                                            <div className="work-card-front">
                                                <img 
                                                    src={work.image} 
                                                    alt={work.title}
                                                    className="work-image"
                                                />
                                                <div className="work-overlay">
                                                    <span className="work-category">{t(work.category)}</span>
                                                    <h3 className="work-title">{work.title}</h3>
                                                </div>
                                            </div>
                                            <div className="work-card-back">
                                                <div className="work-content">
                                                    <span className="work-date">
                                                        {new Date(work.date).toLocaleDateString()}
                                                    </span>
                                                    <h3 className="work-title">{work.title}</h3>
                                                    <p className="work-description">{t(work.description)}</p>
                                                    <button className="work-details-btn">
                                                        {t('viewDetails')}
                                                        <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                                                            <path fillRule="evenodd" d="M10.293 3.293a1 1 0 011.414 0l6 6a1 1 0 010 1.414l-6 6a1 1 0 01-1.414-1.414L14.586 11H3a1 1 0 110-2h11.586l-4.293-4.293a1 1 0 010-1.414z" clipRule="evenodd" />
                                                        </svg>
                                                    </button>
                                                </div>
                                            </div>
                                        </div>
                                    </motion.div>
                                ))}
                            </AnimatePresence>
                        </motion.div>
                    </div>
                </section>
            </main>

            <Footer />
        </div>
    );
}

export default WorksPage;
