import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import Navbar from '../../components/Navbar';
import Footer from '../../components/Footer';
import './WorksPage.css';

const works = [
    {
        id: 1,
        title: "Modern Villa Tasarımı",
        category: "Mimari",
        image: "/images/works/work1.jpg",
        description: "Lüks villa tasarımı ve iç mekan düzenlemesi"
    },
    {
        id: 2,
        title: "Ofis İç Mekan",
        category: "İç Mekan",
        image: "/images/works/work2.jpg",
        description: "Modern ofis iç mekan tasarımı ve uygulaması"
    },
    {
        id: 3,
        title: "Rezidans Projesi",
        category: "Mimari",
        image: "/images/works/work3.jpg",
        description: "Çok katlı rezidans projesi ve peyzaj düzenlemesi"
    },
    {
        id: 4,
        title: "Cafe Tasarımı",
        category: "İç Mekan",
        image: "/images/works/work4.jpg",
        description: "Modern cafe konsept tasarımı ve uygulaması"
    }
];

const categories = ["Tümü", "Mimari", "İç Mekan"];

const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
        opacity: 1,
        transition: {
            staggerChildren: 0.1
        }
    }
};

const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
        opacity: 1,
        y: 0,
        transition: {
            duration: 0.5
        }
    },
    exit: {
        opacity: 0,
        y: -20,
        transition: {
            duration: 0.3
        }
    }
};

function WorksPage() {
    const [selectedCategory, setSelectedCategory] = useState("Tümü");

    const filteredWorks = works.filter(work => 
        selectedCategory === "Tümü" ? true : work.category === selectedCategory
    );

    return (
        <div className="works-page">
            <Navbar />
            
            <section className="works-hero pattern-grid pattern-grid-blue">
                <div className="container mx-auto px-4 py-24 sm:py-32">
                    <motion.h1 
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ duration: 0.5 }}
                        className="text-4xl sm:text-5xl lg:text-6xl font-bold text-center mb-6"
                    >
                        İşlerimiz
                    </motion.h1>
                    <motion.p 
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ duration: 0.5, delay: 0.2 }}
                        className="text-lg sm:text-xl text-gray-300 text-center max-w-3xl mx-auto"
                    >
                        Tamamladığımız projeler ve başarı hikayelerimiz
                    </motion.p>
                </div>
            </section>

            <section className="works-content py-16 sm:py-24">
                <div className="container mx-auto px-4">
                    <div className="flex flex-wrap justify-center gap-4 mb-12">
                        {categories.map((category, index) => (
                            <motion.button
                                key={category}
                                onClick={() => setSelectedCategory(category)}
                                className={`px-6 py-2 rounded-full text-sm font-medium transition-all duration-300
                                    ${selectedCategory === category 
                                        ? 'bg-blue-500 text-white' 
                                        : 'bg-gray-800 text-gray-300 hover:bg-gray-700'}`}
                                initial={{ opacity: 0, y: 20 }}
                                animate={{ opacity: 1, y: 0 }}
                                transition={{ duration: 0.5, delay: index * 0.1 }}
                            >
                                {category}
                            </motion.button>
                        ))}
                    </div>

                    <motion.div 
                        className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8"
                        variants={containerVariants}
                        initial="hidden"
                        animate="visible"
                    >
                        <AnimatePresence mode="wait">
                            {filteredWorks.map(work => (
                                <motion.div
                                    key={work.id}
                                    className="work-card group"
                                    variants={itemVariants}
                                    initial="hidden"
                                    animate="visible"
                                    exit="exit"
                                    layout
                                >
                                    <div className="relative overflow-hidden rounded-xl aspect-[4/3]">
                                        <img 
                                            src={work.image} 
                                            alt={work.title}
                                            className="w-full h-full object-cover transition-transform duration-500 group-hover:scale-110"
                                        />
                                        <div className="absolute inset-0 bg-gradient-to-t from-black/80 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                                            <div className="absolute bottom-0 left-0 right-0 p-6">
                                                <h3 className="text-xl font-semibold mb-2">{work.title}</h3>
                                                <p className="text-gray-300">{work.description}</p>
                                            </div>
                                        </div>
                                    </div>
                                </motion.div>
                            ))}
                        </AnimatePresence>
                    </motion.div>
                </div>
            </section>

            <Footer />
        </div>
    );
}

export default WorksPage;
