import React from 'react';
import { motion } from 'framer-motion';
import { useLanguage } from '../../context/LanguageContext';
import './ServicesPage.css';

const services = [
    {
        id: 'ceramic-coating',
        title: 'ceramicCoating',
        description: 'ceramicDesc',
        image: '/images/services/ceramic-coating.jpg',
        features: ['uvProtection', 'hydrophobic', 'warranty'],
        process: ['inspection', 'preparation', 'application', 'qualityCheck', 'finalDelivery']
    },
    {
        id: 'paint-protection',
        title: 'paintProtection',
        description: 'paintDesc',
        image: '/images/services/paint-protection.jpg',
        features: ['scratchRemoval', 'swirlRemoval', 'paintEnhancement'],
        process: ['inspection', 'preparation', 'application', 'qualityCheck', 'finalDelivery']
    },
    {
        id: 'interior-detailing',
        title: 'interiorDetail',
        description: 'interiorDesc',
        image: '/images/services/interior-detailing.jpg',
        features: ['deepCleaning', 'leatherCare', 'odorRemoval'],
        process: ['inspection', 'preparation', 'application', 'qualityCheck', 'finalDelivery']
    },
    {
        id: 'exterior-detailing',
        title: 'exteriorDetail',
        description: 'exteriorDesc',
        image: '/images/services/exterior-detailing.jpg',
        features: ['handWash', 'clayBar', 'paintSealant'],
        process: ['inspection', 'preparation', 'application', 'qualityCheck', 'finalDelivery']
    }
];

function ServicesPage() {
    const { t } = useLanguage();

    return (
        <div className="services-page">
            <main className="relative z-10">
                <section className="services-hero">
                    <div className="hero-background">
                        <div className="gradient-overlay"></div>
                        <div className="pattern-overlay"></div>
                    </div>
                    <div className="container">
                        <motion.h1
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ duration: 0.5 }}
                            className="text-4xl sm:text-5xl lg:text-6xl font-bold text-center mb-6"
                        >
                            {t('servicesHeroTitle')}
                        </motion.h1>
                        <motion.p
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ duration: 0.5, delay: 0.2 }}
                            className="text-lg sm:text-xl text-gray-300 text-center max-w-3xl mx-auto"
                        >
                            {t('servicesHeroSubtitle')}
                        </motion.p>
                    </div>
                </section>

                <section className="services-grid py-16 sm:py-24">
                    <div className="container mx-auto px-4">
                        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
                            {services.map((service, index) => (
                                <motion.div
                                    key={service.id}
                                    initial={{ opacity: 0, y: 20 }}
                                    whileInView={{ opacity: 1, y: 0 }}
                                    transition={{ duration: 0.5, delay: index * 0.1 }}
                                    viewport={{ once: true }}
                                    className="service-card group"
                                >
                                    <div className="relative overflow-hidden rounded-xl aspect-[16/10] mb-8">
                                        <img
                                            src={service.image}
                                            alt={t(service.title)}
                                            className="w-full h-full object-cover transition-transform duration-500 group-hover:scale-110"
                                        />
                                        <div className="absolute inset-0 bg-gradient-to-t from-black/90 via-black/50 to-transparent">
                                            <div className="absolute bottom-0 left-0 right-0 p-6">
                                                <h3 className="text-2xl font-bold mb-2">
                                                    {t(service.title)}
                                                </h3>
                                                <p className="text-gray-300">
                                                    {t(service.description)}
                                                </p>
                                            </div>
                                        </div>
                                    </div>

                                    <div className="space-y-6">
                                        <div>
                                            <h4 className="text-xl font-semibold mb-4">
                                                {t('serviceFeatures')}
                                            </h4>
                                            <ul className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                                                {service.features.map((feature, idx) => (
                                                    <li key={idx} className="flex items-center space-x-3">
                                                        <svg className="w-5 h-5 text-blue-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                                                        </svg>
                                                        <span>{t(feature)}</span>
                                                    </li>
                                                ))}
                                            </ul>
                                        </div>

                                        <div>
                                            <h4 className="text-xl font-semibold mb-4">
                                                {t('serviceProcess')}
                                            </h4>
                                            <div className="flex flex-wrap gap-4">
                                                {service.process.map((step, idx) => (
                                                    <div key={idx} className="flex items-center space-x-2">
                                                        <span className="w-6 h-6 rounded-full bg-blue-500/20 text-blue-400 flex items-center justify-center text-sm">
                                                            {idx + 1}
                                                        </span>
                                                        <span>{t(step)}</span>
                                                        {idx < service.process.length - 1 && (
                                                            <svg className="w-4 h-4 text-gray-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                                                            </svg>
                                                        )}
                                                    </div>
                                                ))}
                                            </div>
                                        </div>

                                        <button className="w-full px-6 py-3 bg-blue-500 text-white rounded-xl text-sm font-medium hover:bg-blue-600 transition-colors duration-300">
                                            {t('getQuote')}
                                        </button>
                                    </div>
                                </motion.div>
                            ))}
                        </div>
                    </div>
                </section>
            </main>
        </div>
    );
}

export default ServicesPage;
