import React, { useEffect } from 'react'

const IndexPage = () => {
  useEffect(() => {
    const menuToggle = document.getElementById('menu-toggle');
    const mobileMenu = document.getElementById('mobile-menu');

    menuToggle.addEventListener('click', () => {
      mobileMenu.classList.toggle('hidden');

      if (!mobileMenu.classList.contains('hidden')) {
        mobileMenu.classList.add('mobile-menu-enter');
        setTimeout(() => {
          mobileMenu.classList.remove('mobile-menu-enter');
          mobileMenu.classList.add('mobile-menu-enter-active');
        }, 10);
      } else {
        mobileMenu.classList.remove('mobile-menu-enter-active');
        mobileMenu.classList.add('mobile-menu-exit');
        setTimeout(() => {
          mobileMenu.classList.remove('mobile-menu-exit');
        }, 300);
      }
    });
  },[])
  return (
    <div>
      <nav class="bg-white shadow-md fixed w-full z-50">
        <div class="container mx-auto px-4 sm:px-6 lg:px-8">
          <div class="flex items-center justify-between h-16">
            <div class="flex-shrink-0">
              <a href="#" class="text-2xl font-bold text-blue-600">DetayHizmet</a>
            </div>

            <div class="hidden md:flex space-x-4">
              <a href="#home" class="text-gray-700 hover:text-blue-600 px-3 py-2 rounded-md text-sm font-medium">Anasayfa</a>
              <a href="#services" class="text-gray-700 hover:text-blue-600 px-3 py-2 rounded-md text-sm font-medium">Hizmetler</a>
              <a href="#about" class="text-gray-700 hover:text-blue-600 px-3 py-2 rounded-md text-sm font-medium">Hakkımızda</a>
              <a href="#contact" class="text-gray-700 hover:text-blue-600 px-3 py-2 rounded-md text-sm font-medium">İletişim</a>
            </div>

            <div class="md:hidden flex items-center">
              <button id="menu-toggle" class="text-gray-700 hover:text-blue-600 focus:outline-none focus:text-blue-600">
                <svg class="h-6 w-6" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M4 6h16M4 12h16M4 18h16" />
                </svg>
              </button>
            </div>
          </div>
        </div>

        <div id="mobile-menu" class="fixed inset-0 bg-gray-900 bg-opacity-90 flex items-center justify-center hidden">
          <div class="text-center">
            <a href="#home" class="block text-white text-2xl font-semibold py-2 hover:text-blue-400">Anasayfa</a>
            <a href="#services" class="block text-white text-2xl font-semibold py-2 hover:text-blue-400">Hizmetler</a>
            <a href="#about" class="block text-white text-2xl font-semibold py-2 hover:text-blue-400">Hakkımızda</a>
            <a href="#contact" class="block text-white text-2xl font-semibold py-2 hover:text-blue-400">İletişim</a>
            <a href="#booking" class="block bg-blue-600 text-white mt-4 py-2 px-4 rounded-md text-lg font-semibold hover:bg-blue-500">Randevu Al</a>
          </div>
        </div>
      </nav>
    </div>
  )
}

export default IndexPage
