import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useLanguage } from '../../context/LanguageContext';
import './OurWorks.css';

function OurWorks() {
    const { t } = useLanguage();
    const [selectedFilter, setSelectedFilter] = useState('all');

    const filters = [
        { id: 'all', label: t('allWorks') },
        { id: 'detailing', label: t('detailing') },
        { id: 'ceramic', label: t('ceramic') },
        { id: 'paint', label: t('paint') }
    ];

    const works = [
        {
            id: 1,
            title: 'Premium Mobile Detailing',
            category: 'mobile',
            image: '/assets/index/itsnotalwaysintheshop.jpg',
            size: 'large'
        },
        {
            id: 2,
            title: 'Professional Paint Correction',
            category: 'polish',
            image: '/assets/index/brushing.jpg',
            size: 'small'
        },
        {
            id: 3,
            title: 'Interior Detailing',
            category: 'detailing',
            image: '/assets/index/logomockup1.jpg',
            size: 'small'
        },
        {
            id: 4,
            title: 'Ceramic Protection',
            category: 'ceramic',
            image: '/assets/index/ceramicoating.webp',
            size: 'large'
        },
        {
            id: 5,
            title: 'Full Vehicle Detail',
            category: 'detailing',
            image: '/assets/index/cleancar.jpg',
            size: 'large'
        },
        {
            id: 6,
            title: 'Paint Enhancement',
            category: 'polish',
            image: '/assets/index/polishing_2.jpg',
            size: 'small'
        }
    ];

    const filteredWorks = selectedFilter === 'all' 
        ? works 
        : works.filter(work => work.category === selectedFilter);

    return (
        <section className="works-section">
            <div className="container">
                <motion.div
                    className="section-header"
                    initial={{ opacity: 0, y: 20 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.6 }}
                >
                    <h2>{t('ourWorks')}</h2>
                    <p>{t('worksSubtitle')}</p>
                </motion.div>

                <div className="filters">
                    {filters.map(filter => (
                        <button
                            key={filter.id}
                            className={`filter-button ${selectedFilter === filter.id ? 'active' : ''}`}
                            onClick={() => setSelectedFilter(filter.id)}
                        >
                            {filter.label}
                        </button>
                    ))}
                </div>

                <motion.div className="works-grid">
                    <AnimatePresence mode="wait">
                        {filteredWorks.map((work, index) => (
                            <motion.div
                                key={work.id}
                                className="work-card"
                                initial={{ opacity: 0, y: 20 }}
                                animate={{ opacity: 1, y: 0 }}
                                exit={{ opacity: 0, y: -20 }}
                                transition={{ duration: 0.5, delay: index * 0.1 }}
                            >
                                <div className="work-image">
                                    <img src={work.image} alt={work.title} />
                                    <div className="work-overlay">
                                        <h3>{work.title}</h3>
                                        <p>{work.description}</p>
                                        <button className="view-details">
                                            {t('viewDetails')}
                                            <i className="fas fa-arrow-right"></i>
                                        </button>
                                    </div>
                                </div>
                            </motion.div>
                        ))}
                    </AnimatePresence>
                </motion.div>
            </div>
        </section>
    );
}

export default OurWorks;
