import React, { createContext, useState, useContext } from 'react';

const translations = {
    tr: {
        // Navbar
        home: "Ana Sayfa",
        services: "Hizmetlerimiz",
        works: "Çalışmalarımız",
        cars: "Araçlar",
        contact: "İletişim",

        // Hero Sections
        welcomeTitle: "Premium Araç Bakım ve Koruma",
        welcomeDescription: "Aracınız için profesyonel detailing hizmetleri",
        exploreButton: "Hizmetlerimizi Keşfedin",

        // Car Gallery
        carGalleryTitle: "Araç Bilgi Sistemi",
        carGalleryDescription: "Aracınızın detaylarını görüntülemek için ID numarasını girin veya galeriden seçim yapın",
        searchPlaceholder: "Araç ID veya marka/model ile arama yapın...",

        // Services
        ceramicCoating: "Seramik Kaplama",
        paintProtection: "Boya Koruma",
        interiorDetailing: "İç Detaylama",
        paintCorrection: "Boya Düzeltme",

        // Footer
        footerDescription: "Profesyonel detailing hizmetleri ile otomotiv estetiğini yükseltiyoruz. Aracınız en iyi bakımı hak ediyor.",
        followUs: "Bizi Takip Edin",
    },
    en: {
        // Navbar
        home: "Home",
        services: "Services",
        works: "Our Works",
        cars: "Cars",
        contact: "Contact",

        // Hero Sections
        welcomeTitle: "Premium Car Care & Protection",
        welcomeDescription: "Professional detailing services for your vehicle",
        exploreButton: "Explore Our Services",

        // Car Gallery
        carGalleryTitle: "Car Information System",
        carGalleryDescription: "Enter your car's ID number or browse the gallery to view details",
        searchPlaceholder: "Search by Car ID or brand/model...",

        // Services
        ceramicCoating: "Ceramic Coating",
        paintProtection: "Paint Protection",
        interiorDetailing: "Interior Detailing",
        paintCorrection: "Paint Correction",

        // Footer
        footerDescription: "Elevating automotive aesthetics with premium detailing services. Your car deserves the best care.",
        followUs: "Follow Us",
    }
};

const LanguageContext = createContext();

export const LanguageProvider = ({ children }) => {
    const [language, setLanguage] = useState('tr'); // Default language is Turkish

    const toggleLanguage = () => {
        setLanguage(prev => prev === 'tr' ? 'en' : 'tr');
    };

    const t = (key) => {
        return translations[language][key] || key;
    };

    return (
        <LanguageContext.Provider value={{ language, toggleLanguage, t }}>
            {children}
        </LanguageContext.Provider>
    );
};

export const useLanguage = () => {
    const context = useContext(LanguageContext);
    if (!context) {
        throw new Error('useLanguage must be used within a LanguageProvider');
    }
    return context;
};
