import React from 'react';
import { motion } from 'framer-motion';
import Navbar from '../../components/Navbar';
import Footer from '../../components/Footer';
import { useLanguage } from '../../context/LanguageContext';
import './ServicesPage.css';

const ServicesPage = () => {
  const { t } = useLanguage();

  const services = [
    {
      id: 'ceramicCoating',
      title: 'Seramik Kaplama',
      subtitle: 'Aracınız için uzun ömürlü koruma',
      description: 'Seramik kaplama, aracınızın boyasını dış etkenlere karşı koruyan ve parlak görünümünü uzun süre muhafaza eden ileri teknoloji bir uygulamadır.',
      image: 'https://images.unsplash.com/photo-1573950940509-d924ee3fd345?q=80&w=1000',
      icon: 'shield',
      benefits: [
        {
          icon: 'clock',
          title: 'Uzun Ömürlü Koruma',
          description: '5+ yıl boyunca aracınızı korur',
          image: 'https://images.unsplash.com/photo-1596636478939-6c22e85a3ccc?q=80&w=1000'
        },
        {
          icon: 'sun',
          title: 'UV Koruması',
          description: 'Güneş ışınlarının zararlı etkilerine karşı maksimum koruma',
          image: 'https://images.unsplash.com/photo-1607603750909-408e193868c7?q=80&w=1000'
        },
        {
          icon: 'tint',
          title: 'Hidrofobik Etki',
          description: 'Su ve kir tutmayan yüzey özelliği',
          image: 'https://images.unsplash.com/photo-1594051673969-172a6f721d3c?q=80&w=1000'
        }
      ],
      details: {
        preparation: [
          'Detaylı yüzey temizliği',
          'Kil uygulaması',
          'Pasta ve polish işlemleri',
          'Yüzey hazırlığı ve IPA temizliği'
        ],
        application: [
          'Profesyonel seramik kaplama uygulaması',
          '24 saat kontrollü ortamda kuruma',
          'Son kontrol ve detaylı temizlik'
        ],
        maintenance: [
          'Düzenli bakım önerileri',
          'Yıkama teknikleri',
          'Periyodik kontrol tavsiyeleri'
        ]
      }
    },
    {
      id: 'paintProtection',
      title: 'Boya Koruma',
      subtitle: 'Profesyonel boya koruma sistemleri',
      description: 'Aracınızın boyasını çizilmelere, UV ışınlarına ve kimyasal etkilere karşı koruyan kapsamlı boya koruma hizmetleri.',
      image: 'https://images.unsplash.com/photo-1558486012-817176f84c6d?q=80&w=1000',
      icon: 'car',
      benefits: [
        {
          icon: 'shield-alt',
          title: 'Çizilme Koruması',
          description: 'Hafif çiziklere karşı etkin koruma',
          image: 'https://images.unsplash.com/photo-1621155346337-1d19476ba7d6?q=80&w=1000'
        },
        {
          icon: 'magic',
          title: 'Parlaklık',
          description: 'Showroom parlaklığında görünüm',
          image: 'https://images.unsplash.com/photo-1618843479313-40f8afb4b4d8?q=80&w=1000'
        },
        {
          icon: 'flask',
          title: 'Kimyasal Direnç',
          description: 'Asit yağmuru ve kuş pisliğine karşı koruma',
          image: 'https://images.unsplash.com/photo-1600880292203-757bb62b4baf?q=80&w=1000'
        }
      ],
      details: {
        preparation: [
          'Boya yüzeyinin detaylı analizi',
          'Boya kalınlığı ölçümü',
          'Yüzey temizliği ve hazırlığı'
        ],
        application: [
          'Koruyucu boya filmi uygulaması',
          'Kenar ve köşe koruması',
          'Şeffaf koruyucu kaplama'
        ],
        maintenance: [
          'Periyodik bakım tavsiyeleri',
          'Doğru yıkama teknikleri',
          'Koruma ömrünü uzatma önerileri'
        ]
      }
    },
    {
      id: 'interiorDetail',
      title: 'İç Detay Temizlik',
      subtitle: 'Kapsamlı iç mekan bakımı',
      description: 'Aracınızın iç mekanını derinlemesine temizleyen, dezenfekte eden ve koruyan profesyonel detaylı temizlik hizmeti.',
      image: 'https://images.unsplash.com/photo-1570303345338-e1f1500c4f5a?q=80&w=1000',
      icon: 'couch',
      benefits: [
        {
          icon: 'broom',
          title: 'Derinlemesine Temizlik',
          description: 'En ince ayrıntısına kadar temizlik',
          image: 'https://images.unsplash.com/photo-1507136566006-cfc505b114fc?q=80&w=1000'
        },
        {
          icon: 'pump-soap',
          title: 'Hijyenik Temizlik',
          description: 'Antibakteriyel ve antialerjik uygulama',
          image: 'https://images.unsplash.com/photo-1513360371669-4adf3dd7dff8?q=80&w=1000'
        },
        {
          icon: 'wind',
          title: 'Koku Giderme',
          description: 'Özel sistem ile koku giderme',
          image: 'https://images.unsplash.com/photo-1511578314322-379afb476865?q=80&w=1000'
        }
      ],
      details: {
        preparation: [
          'Detaylı iç mekan analizi',
          'Koltuk ve döşeme kontrolü',
          'Leke ve kir tespiti'
        ],
        application: [
          'Koltuk ve döşeme temizliği',
          'Tavan ve plastik aksamların temizliği',
          'Torpido ve konsol bakımı'
        ],
        maintenance: [
          'Düzenli bakım önerileri',
          'Koltuk koruma tavsiyeleri',
          'İç mekan bakım ipuçları'
        ]
      }
    },
    {
      id: 'exteriorDetail',
      title: 'Dış Detay Temizlik',
      subtitle: 'Profesyonel dış yüzey bakımı',
      description: 'Aracınızın dış yüzeyini derinlemesine temizleyen, cilalayan ve koruyan kapsamlı bakım hizmeti.',
      image: 'https://images.unsplash.com/photo-1535732820275-9ffd998cac22?q=80&w=1000',
      icon: 'car-side',
      benefits: [
        {
          icon: 'shower',
          title: 'Detaylı Yıkama',
          description: 'İki aşamalı özel şampuan sistemi',
          image: 'https://images.unsplash.com/photo-1520340356584-f9917d1eea6f?q=80&w=1000'
        },
        {
          icon: 'magic',
          title: 'Pasta Cila',
          description: 'Profesyonel pasta ve cila uygulaması',
          image: 'https://images.unsplash.com/photo-1607860108855-64acf2078ed9?q=80&w=1000'
        },
        {
          icon: 'spray-can',
          title: 'Wax Koruma',
          description: 'Uzun ömürlü wax koruma',
          image: 'https://images.unsplash.com/photo-1605515298946-d062f2e9da53?q=80&w=1000'
        }
      ],
      details: {
        preparation: [
          'Detaylı dış yüzey analizi',
          'Boya kontrolü',
          'Yüzey kontaminasyon tespiti'
        ],
        application: [
          'Snow foam ön yıkama',
          'İki aşamalı yıkama sistemi',
          'Demir tozu temizliği'
        ],
        maintenance: [
          'Düzenli bakım tavsiyeleri',
          'Doğru yıkama teknikleri',
          'Mevsimsel bakım önerileri'
        ]
      }
    }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.5
      }
    }
  };

  return (
    <div className="services-page">
      <Navbar />
      
      <main>
        {/* Hero Section */}
        <section className="services-hero">
          <div className="hero-background">
            <div className="gradient-overlay"></div>
            <div className="pattern-overlay"></div>
          </div>
          <div className="container">
            <motion.div 
              className="hero-content"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6 }}
            >
              <h1>Profesyonel Araç Bakım Hizmetlerimiz</h1>
              <p>Aracınızın değerini ve görünümünü en üst seviyede tutmak için uzman kadromuz ve ileri teknoloji ekipmanlarımızla hizmetinizdeyiz.</p>
            </motion.div>
          </div>
        </section>

        {/* Services Section */}
        {services.map((service) => (
          <motion.section 
            key={service.id}
            className="service-detail-section"
            initial={{ opacity: 0 }}
            whileInView={{ opacity: 1 }}
            viewport={{ once: true }}
          >
            <div className="container">
              <div className="service-detail-grid">
                <div className="service-detail-content">
                  <motion.div 
                    className="service-header"
                    initial={{ opacity: 0, x: -50 }}
                    whileInView={{ opacity: 1, x: 0 }}
                    viewport={{ once: true }}
                  >
                    <div className="service-icon">
                      <i className={`fas fa-${service.icon}`}></i>
                    </div>
                    <div>
                      <h2>{service.title}</h2>
                      <p>{service.subtitle}</p>
                    </div>
                  </motion.div>

                  <motion.p 
                    className="service-description"
                    initial={{ opacity: 0, y: 20 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    viewport={{ once: true }}
                  >
                    {service.description}
                  </motion.p>

                  <motion.div 
                    className="service-benefits"
                    variants={containerVariants}
                    initial="hidden"
                    whileInView="visible"
                    viewport={{ once: true }}
                  >
                    {service.benefits.map((benefit, index) => (
                      <motion.div 
                        key={index} 
                        className="benefit-card"
                        variants={itemVariants}
                      >
                        <div className="benefit-image">
                          <img src={benefit.image} alt={benefit.title} />
                        </div>
                        <div className="benefit-content">
                          <i className={`fas fa-${benefit.icon}`}></i>
                          <h3>{benefit.title}</h3>
                          <p>{benefit.description}</p>
                        </div>
                      </motion.div>
                    ))}
                  </motion.div>

                  <motion.button 
                    className="book-service-btn"
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                  >
                    Randevu Al
                    <i className="fas fa-arrow-right"></i>
                  </motion.button>
                </div>

                <motion.div 
                  className="service-detail-image"
                  initial={{ opacity: 0, x: 50 }}
                  whileInView={{ opacity: 1, x: 0 }}
                  viewport={{ once: true }}
                >
                  <img src={service.image} alt={service.title} />
                </motion.div>
              </div>

              <motion.div 
                className="service-process"
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
              >
                <div className="process-section">
                  <h3>
                    <i className="fas fa-tools"></i>
                    Hazırlık Aşaması
                  </h3>
                  <ul>
                    {service.details.preparation.map((step, index) => (
                      <li key={index}>
                        <i className="fas fa-check"></i>
                        {step}
                      </li>
                    ))}
                  </ul>
                </div>

                <div className="process-section">
                  <h3>
                    <i className="fas fa-spray-can"></i>
                    Uygulama Süreci
                  </h3>
                  <ul>
                    {service.details.application.map((step, index) => (
                      <li key={index}>
                        <i className="fas fa-check"></i>
                        {step}
                      </li>
                    ))}
                  </ul>
                </div>

                <div className="process-section">
                  <h3>
                    <i className="fas fa-clipboard-list"></i>
                    Bakım Önerileri
                  </h3>
                  <ul>
                    {service.details.maintenance.map((step, index) => (
                      <li key={index}>
                        <i className="fas fa-check"></i>
                        {step}
                      </li>
                    ))}
                  </ul>
                </div>
              </motion.div>
            </div>
          </motion.section>
        ))}
      </main>

      <Footer />
    </div>
  );
};

export default ServicesPage;
