import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useLanguage } from '../../context/LanguageContext';
import './WorksPage.css';

const works = [
    {
        id: 1,
        title: 'BMW M4 Competition',
        category: 'ceramicCoating',
        image: '/images/works/bmw-m4.jpg',
        date: '2023-12-15'
    },
    {
        id: 2,
        title: 'Mercedes-AMG GT',
        category: 'paintProtection',
        image: '/images/works/mercedes-amg.jpg',
        date: '2023-12-10'
    },
    // Add more works as needed
];

const categories = [
    { id: 'all', name: 'allWorks' },
    { id: 'interiorDetail', name: 'interiorWorks' },
    { id: 'exteriorDetail', name: 'exteriorWorks' },
    { id: 'paintProtection', name: 'paintProtectionWorks' },
    { id: 'ceramicCoating', name: 'ceramicCoatingWorks' }
];

function WorksPage() {
    const [selectedCategory, setSelectedCategory] = useState('all');
    const { t } = useLanguage();

    const filteredWorks = works.filter(work => 
        selectedCategory === 'all' || work.category === selectedCategory
    );

    return (
        <div className="works-page">
            <main className="relative z-10 pt-[60px]">
                <section className="works-hero pattern-grid pattern-grid-blue">
                    <div className="container mx-auto px-4 py-16 sm:py-24">
                        <motion.h1
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ duration: 0.5 }}
                            className="text-4xl sm:text-5xl lg:text-6xl font-bold text-center mb-6"
                        >
                            {t('worksTitle')}
                        </motion.h1>
                        <motion.p
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ duration: 0.5, delay: 0.2 }}
                            className="text-lg sm:text-xl text-gray-300 text-center max-w-3xl mx-auto"
                        >
                            {t('worksSubtitle')}
                        </motion.p>
                    </div>
                </section>

                <section className="works-content py-16 sm:py-24">
                    <div className="container mx-auto px-4">
                        <div className="flex flex-wrap justify-center gap-4 mb-12">
                            {categories.map(category => (
                                <button
                                    key={category.id}
                                    onClick={() => setSelectedCategory(category.id)}
                                    className={`px-6 py-2 rounded-full text-sm font-medium transition-all duration-300
                                        ${selectedCategory === category.id 
                                            ? 'bg-blue-600 text-white shadow-lg shadow-blue-500/25' 
                                            : 'bg-gray-800/50 text-gray-300 hover:bg-gray-700/50 hover:text-white'}`}
                                >
                                    {t(category.name)}
                                </button>
                            ))}
                        </div>

                        {filteredWorks.length === 0 ? (
                            <motion.p
                                initial={{ opacity: 0 }}
                                animate={{ opacity: 1 }}
                                className="text-center text-gray-400"
                            >
                                {t('noWorksFound')}
                            </motion.p>
                        ) : (
                            <motion.div
                                className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8"
                                initial={{ opacity: 0 }}
                                animate={{ opacity: 1 }}
                                transition={{ duration: 0.5 }}
                            >
                                <AnimatePresence mode="wait">
                                    {filteredWorks.map(work => (
                                        <motion.div
                                            key={work.id}
                                            layout
                                            initial={{ opacity: 0, y: 20 }}
                                            animate={{ opacity: 1, y: 0 }}
                                            exit={{ opacity: 0, y: -20 }}
                                            className="work-card group"
                                        >
                                            <div className="relative overflow-hidden rounded-xl aspect-[4/3]">
                                                <img
                                                    src={work.image}
                                                    alt={work.title}
                                                    className="w-full h-full object-cover transition-transform duration-500 group-hover:scale-110"
                                                />
                                                <div className="absolute inset-0 bg-gradient-to-t from-black/90 via-black/50 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                                                    <div className="absolute bottom-0 left-0 right-0 p-6">
                                                        <span className="inline-block px-3 py-1 rounded-full text-xs font-medium bg-blue-500/20 text-blue-400 mb-3">
                                                            {t(work.category)}
                                                        </span>
                                                        <h3 className="text-xl font-semibold mb-2">{work.title}</h3>
                                                        <span className="text-sm text-gray-400">
                                                            {new Date(work.date).toLocaleDateString(t('locale'))}
                                                        </span>
                                                        <button className="mt-4 px-6 py-2 bg-blue-500 text-white rounded-full text-sm font-medium hover:bg-blue-600 transition-colors duration-300">
                                                            {t('viewDetails')}
                                                        </button>
                                                    </div>
                                                </div>
                                            </div>
                                        </motion.div>
                                    ))}
                                </AnimatePresence>
                            </motion.div>
                        )}
                    </div>
                </section>
            </main>
        </div>
    );
}

export default WorksPage;
