import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import Navbar from '../../components/Navbar';
import Footer from '../../components/Footer';
import './CarGallery.css';

// Temporary data for demonstration
const cars = [
    {
        id: "DX2023001",
        brand: "BMW",
        model: "M4 Competition",
        year: 2023,
        color: "Sao Paulo Yellow",
        services: ["Ceramic Coating", "Paint Protection Film"],
        mainImage: "/images/cars/bmw-m4.jpg",
        date: "2023-12-15"
    },
    {
        id: "DX2023002",
        brand: "Mercedes",
        model: "AMG GT",
        year: 2022,
        color: "Selenite Grey",
        services: ["Paint Correction", "Interior Detailing"],
        mainImage: "/images/cars/mercedes-amg.jpg",
        date: "2023-12-10"
    },
    // Add more cars as needed
];

function CarGallery() {
    const [searchQuery, setSearchQuery] = useState('');
    const [selectedBrand, setSelectedBrand] = useState('all');
    const [selectedService, setSelectedService] = useState('all');

    const filteredCars = cars.filter(car => {
        const matchesSearch = 
            car.id.toLowerCase().includes(searchQuery.toLowerCase()) ||
            car.brand.toLowerCase().includes(searchQuery.toLowerCase()) ||
            car.model.toLowerCase().includes(searchQuery.toLowerCase());
        
        const matchesBrand = selectedBrand === 'all' || car.brand === selectedBrand;
        const matchesService = selectedService === 'all' || car.services.includes(selectedService);

        return matchesSearch && matchesBrand && matchesService;
    });

    return (
        <div className="car-gallery-page">
            <Navbar />
            
            <main className="relative z-10 pt-[72px]">
                <section className="gallery-hero pattern-grid pattern-grid-blue">
                    <div className="container mx-auto px-4 py-16">
                        <motion.h1 
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ duration: 0.5 }}
                            className="text-4xl sm:text-5xl font-bold text-center mb-4"
                        >
                            Araç Bilgi Sistemi
                        </motion.h1>
                        <motion.p 
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ duration: 0.5, delay: 0.2 }}
                            className="text-lg text-gray-300 text-center max-w-3xl mx-auto"
                        >
                            Aracınızın detaylarını görüntülemek için ID numarasını girin veya galeriden seçim yapın
                        </motion.p>
                    </div>
                </section>

                <section className="search-section">
                    <div className="container mx-auto px-4">
                        <motion.div 
                            className="search-box-container"
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ duration: 0.5 }}
                        >
                            <div className="search-box-inner">
                                <div className="search-icon">
                                    <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                                    </svg>
                                </div>
                                <input
                                    type="text"
                                    placeholder="Araç ID veya marka/model ile arama yapın..."
                                    value={searchQuery}
                                    onChange={(e) => setSearchQuery(e.target.value)}
                                    className="search-input"
                                />
                                {searchQuery && (
                                    <button 
                                        onClick={() => setSearchQuery('')}
                                        className="clear-button"
                                    >
                                        <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                                            <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                                        </svg>
                                    </button>
                                )}
                            </div>
                        </motion.div>
                    </div>
                </section>

                <section className="cars-grid py-16 sm:py-24">
                    <div className="container mx-auto px-4">
                        <motion.div 
                            className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8"
                            initial={{ opacity: 0 }}
                            animate={{ opacity: 1 }}
                            transition={{ duration: 0.5 }}
                        >
                            <AnimatePresence mode="wait">
                                {filteredCars.map(car => (
                                    <motion.div
                                        key={car.id}
                                        layout
                                        initial={{ opacity: 0, y: 20 }}
                                        animate={{ opacity: 1, y: 0 }}
                                        exit={{ opacity: 0, y: -20 }}
                                        className="car-card group"
                                    >
                                        <Link to={`/cars/${car.id}`} className="block">
                                            <div className="relative overflow-hidden rounded-xl aspect-[16/10]">
                                                <img 
                                                    src={car.mainImage} 
                                                    alt={`${car.brand} ${car.model}`}
                                                    className="w-full h-full object-cover transition-transform duration-500 group-hover:scale-110"
                                                />
                                                <div className="absolute inset-0 bg-gradient-to-t from-black/90 via-black/50 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                                                    <div className="absolute bottom-0 left-0 right-0 p-6">
                                                        <div className="flex items-center justify-between mb-3">
                                                            <span className="text-sm font-medium text-blue-400">
                                                                {car.id}
                                                            </span>
                                                            <span className="text-sm text-gray-400">
                                                                {new Date(car.date).toLocaleDateString('tr-TR')}
                                                            </span>
                                                        </div>
                                                        <h3 className="text-xl font-semibold mb-2">
                                                            {car.brand} {car.model}
                                                        </h3>
                                                        <div className="flex flex-wrap gap-2">
                                                            {car.services.map((service, index) => (
                                                                <span 
                                                                    key={index}
                                                                    className="inline-block px-3 py-1 rounded-full text-xs font-medium bg-blue-500/20 text-blue-400"
                                                                >
                                                                    {service}
                                                                </span>
                                                            ))}
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </Link>
                                    </motion.div>
                                ))}
                            </AnimatePresence>
                        </motion.div>
                    </div>
                </section>
            </main>

            <Footer />
        </div>
    );
}

export default CarGallery;
