import React from 'react';
import { motion } from 'framer-motion';
import { useLanguage } from '../../context/LanguageContext';
import './WorksPage.css';

const works = [
  {
    id: 1,
    image: '/images/works/work1.jpg',
    category: 'paintProtection',
    size: 'large'
  },
  {
    id: 2,
    image: '/images/works/work2.jpg',
    category: 'ceramicCoating',
    size: 'small'
  },
  {
    id: 3,
    image: '/images/works/work3.jpg',
    category: 'interiorDetail',
    size: 'large'
  },
  {
    id: 4,
    image: '/images/works/work4.jpg',
    category: 'exteriorDetail',
    size: 'small'
  },
  {
    id: 5,
    image: '/images/works/work5.jpg',
    category: 'paintProtection',
    size: 'small'
  },
  {
    id: 6,
    image: '/images/works/work6.jpg',
    category: 'ceramicCoating',
    size: 'large'
  },
  {
    id: 7,
    image: '/images/works/work7.jpg',
    category: 'interiorDetail',
    size: 'small'
  },
  {
    id: 8,
    image: '/images/works/work8.jpg',
    category: 'exteriorDetail',
    size: 'large'
  }
];

const WorksPage = () => {
  const { t } = useLanguage();

  return (
    <div className="works-page">
      <section className="works-hero pattern-grid pattern-grid-blue">
        <div className="container mx-auto px-4 py-24">
          <motion.h1
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            className="text-5xl font-bold text-center mb-6"
          >
            {t('worksTitle')}
          </motion.h1>
          <motion.p
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            className="text-xl text-gray-300 text-center max-w-3xl mx-auto"
          >
            {t('worksSubtitle')}
          </motion.p>
        </div>
      </section>

      <section className="photo-gallery">
        <div className="masonry-grid">
          {works.map((work) => (
            <motion.div
              key={work.id}
              className="masonry-item"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6 }}
            >
              <div className="photo-card">
                <img
                  src={work.image}
                  alt={t(`works.${work.category}`)}
                  className="photo"
                  loading="lazy"
                />
              </div>
            </motion.div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default WorksPage;
