import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { useLanguage } from '../context/LanguageContext';
import LanguageToggle from './LanguageToggle';
import './Navbar.css';

const Navbar = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [isScrolled, setIsScrolled] = useState(false);
    const location = useLocation();
    const { t } = useLanguage();

    // ... keep existing scroll effect code ...

    return (
        <nav className={`navbar ${isScrolled ? 'scrolled' : ''}`}>
            <div className="container mx-auto px-4">
                <div className="nav-content">
                    <Link to="/" className="logo">
                        DetailinX
                    </Link>

                    <div className="nav-right">
                        <div className={`nav-links ${isOpen ? 'active' : ''}`}>
                            <Link to="/" className={location.pathname === '/' ? 'active' : ''}>
                                {t('home')}
                            </Link>
                            <Link to="/hizmetler" className={location.pathname === '/hizmetler' ? 'active' : ''}>
                                {t('services')}
                            </Link>
                            <Link to="/calismalarimiz" className={location.pathname === '/calismalarimiz' ? 'active' : ''}>
                                {t('works')}
                            </Link>
                            <Link to="/arabalar" className={location.pathname === '/arabalar' ? 'active' : ''}>
                                {t('cars')}
                            </Link>
                            <Link to="/iletisim" className={location.pathname === '/iletisim' ? 'active' : ''}>
                                {t('contact')}
                            </Link>
                            <LanguageToggle />
                        </div>

                        <button 
                            className="mobile-menu-button"
                            onClick={() => setIsOpen(!isOpen)}
                            aria-label="Toggle menu"
                        >
                            <div className={`hamburger ${isOpen ? 'active' : ''}`}>
                                <span></span>
                                <span></span>
                                <span></span>
                            </div>
                        </button>
                    </div>
                </div>
            </div>

            <AnimatePresence>
                {isOpen && (
                    <motion.div 
                        className="mobile-menu"
                        initial={{ opacity: 0, y: -20 }}
                        animate={{ opacity: 1, y: 0 }}
                        exit={{ opacity: 0, y: -20 }}
                        transition={{ duration: 0.3 }}
                    >
                        <Link to="/" onClick={() => setIsOpen(false)}>
                            {t('home')}
                        </Link>
                        <Link to="/hizmetler" onClick={() => setIsOpen(false)}>
                            {t('services')}
                        </Link>
                        <Link to="/calismalarimiz" onClick={() => setIsOpen(false)}>
                            {t('works')}
                        </Link>
                        <Link to="/arabalar" onClick={() => setIsOpen(false)}>
                            {t('cars')}
                        </Link>
                        <Link to="/iletisim" onClick={() => setIsOpen(false)}>
                            {t('contact')}
                        </Link>
                        <div className="mobile-language-toggle">
                            <LanguageToggle />
                        </div>
                    </motion.div>
                )}
            </AnimatePresence>
        </nav>
    );
};

export default Navbar;
