import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import { motion, AnimatePresence } from 'framer-motion';
import LanguageSwitcher from './LanguageSwitcher';
import './Navbar.css';

function Navbar() {
    const [isOpen, setIsOpen] = useState(false);
    const [isScrolled, setIsScrolled] = useState(false);
    const location = useLocation();
    const { t } = useTranslation();

    useEffect(() => {
        const handleScroll = () => {
            setIsScrolled(window.scrollY > 20);
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    useEffect(() => {
        setIsOpen(false);
    }, [location]);

    return (
        <nav className={`navbar ${isScrolled ? 'scrolled' : ''}`}>
            <div className="nav-container">
                <Link to="/" className="logo">
                    DetailinX
                </Link>

                <div className="nav-right">
                    <LanguageSwitcher />

                    <button 
                        className="mobile-menu-button"
                        onClick={() => setIsOpen(!isOpen)}
                        aria-label="Toggle menu"
                    >
                        <div className={`hamburger ${isOpen ? 'open' : ''}`}>
                            <span></span>
                            <span></span>
                            <span></span>
                        </div>
                    </button>

                    <div className={`nav-links ${isOpen ? 'open' : ''}`}>
                        <Link to="/" className="nav-link">
                            {t('nav.home')}
                        </Link>
                        <Link to="/hizmetler" className="nav-link">
                            {t('nav.services')}
                        </Link>
                        <Link to="/galeri" className="nav-link">
                            {t('nav.works')}
                        </Link>
                        <Link to="/arabalar" className="nav-link">
                            {t('nav.cars')}
                        </Link>
                        <Link to="/iletisim" className="nav-link">
                            {t('nav.contact')}
                        </Link>
                    </div>
                </div>

                <AnimatePresence>
                    {isOpen && (
                        <motion.div 
                            className="mobile-menu"
                            initial={{ opacity: 0, y: -20 }}
                            animate={{ opacity: 1, y: 0 }}
                            exit={{ opacity: 0, y: -20 }}
                            transition={{ duration: 0.2 }}
                        >
                            <Link to="/" className="mobile-link">
                                {t('nav.home')}
                            </Link>
                            <Link to="/hizmetler" className="mobile-link">
                                {t('nav.services')}
                            </Link>
                            <Link to="/galeri" className="mobile-link">
                                {t('nav.works')}
                            </Link>
                            <Link to="/arabalar" className="mobile-link">
                                {t('nav.cars')}
                            </Link>
                            <Link to="/iletisim" className="mobile-link">
                                {t('nav.contact')}
                            </Link>
                        </motion.div>
                    )}
                </AnimatePresence>
            </div>
        </nav>
    );
}

export default Navbar;
