import React from 'react';
import { motion } from 'framer-motion';
import Navbar from '../../components/Navbar';
import Footer from '../../components/Footer';
import { useLanguage } from '../../context/LanguageContext';
import './ServicesPage.css';

const ServicesPage = () => {
  const { t } = useLanguage();

  const services = [
    {
      id: 'ceramicCoating',
      title: 'ceramicCoating',
      description: 'ceramicDesc',
      image: 'https://images.unsplash.com/photo-1573950940509-d924ee3fd345?q=80&w=1000',
      icon: 'shield',
      benefits: [
        { icon: 'clock', name: 'durability', description: 'durabilityBenefitDesc' },
        { icon: 'sun', name: 'shine', description: 'shineBenefitDesc' },
        { icon: 'shield-alt', name: 'protection', description: 'protectionBenefitDesc' }
      ],
      process: [
        { step: 1, name: 'inspection', description: 'inspectionStepDesc' },
        { step: 2, name: 'preparation', description: 'preparationStepDesc' },
        { step: 3, name: 'application', description: 'applicationStepDesc' },
        { step: 4, name: 'curing', description: 'curingStepDesc' }
      ],
      duration: 'duration2Days',
      warranty: 'warranty5Years'
    },
    {
      id: 'paintProtection',
      title: 'paintProtection',
      description: 'paintDesc',
      image: 'https://images.unsplash.com/photo-1558486012-817176f84c6d?q=80&w=1000',
      icon: 'car',
      benefits: [
        { icon: 'band-aid', name: 'scratch', description: 'scratchBenefitDesc' },
        { icon: 'sun', name: 'uv', description: 'uvBenefitDesc' },
        { icon: 'flask', name: 'chemical', description: 'chemicalBenefitDesc' }
      ],
      process: [
        { step: 1, name: 'surfacePrep', description: 'surfacePrepStepDesc' },
        { step: 2, name: 'filmApplication', description: 'filmApplicationStepDesc' },
        { step: 3, name: 'edgeSealing', description: 'edgeSealingStepDesc' }
      ],
      duration: 'duration1Day',
      warranty: 'warranty3Years'
    },
    {
      id: 'interiorDetail',
      title: 'interiorDetail',
      description: 'interiorDesc',
      image: 'https://images.unsplash.com/photo-1570303345338-e1f1500c4f5a?q=80&w=1000',
      icon: 'couch',
      benefits: [
        { icon: 'broom', name: 'deepClean', description: 'deepCleanBenefitDesc' },
        { icon: 'pump-soap', name: 'sanitize', description: 'sanitizeBenefitDesc' },
        { icon: 'shield-alt', name: 'protect', description: 'protectBenefitDesc' }
      ],
      process: [
        { step: 1, name: 'vacuuming', description: 'vacuumingStepDesc' },
        { step: 2, name: 'steamCleaning', description: 'steamCleaningStepDesc' },
        { step: 3, name: 'leatherCare', description: 'leatherCareStepDesc' },
        { step: 4, name: 'finishing', description: 'finishingStepDesc' }
      ],
      duration: 'duration4Hours',
      warranty: 'satisfaction30Days'
    },
    {
      id: 'exteriorDetail',
      title: 'exteriorDetail',
      description: 'exteriorDesc',
      image: 'https://images.unsplash.com/photo-1535732820275-9ffd998cac22?q=80&w=1000',
      icon: 'car-side',
      benefits: [
        { icon: 'shower', name: 'wash', description: 'washBenefitDesc' },
        { icon: 'magic', name: 'polish', description: 'polishBenefitDesc' },
        { icon: 'spray-can', name: 'wax', description: 'waxBenefitDesc' }
      ],
      process: [
        { step: 1, name: 'contactlessWash', description: 'contactlessWashStepDesc' },
        { step: 2, name: 'clayBar', description: 'clayBarStepDesc' },
        { step: 3, name: 'polish', description: 'polishStepDesc' },
        { step: 4, name: 'sealant', description: 'sealantStepDesc' }
      ],
      duration: 'duration1Day',
      warranty: 'satisfaction14Days'
    }
  ];

  const features = [
    {
      icon: 'medal',
      title: 'expertTeam',
      description: 'expertTeamDesc'
    },
    {
      icon: 'tools',
      title: 'premiumProducts',
      description: 'premiumProductsDesc'
    },
    {
      icon: 'clock',
      title: 'timelyService',
      description: 'timelyServiceDesc'
    },
    {
      icon: 'heart',
      title: 'satisfaction',
      description: 'satisfactionDesc'
    }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.5
      }
    }
  };

  return (
    <div className="services-page">
      <Navbar />
      
      <main>
        {/* Hero Section */}
        <section className="services-hero">
          <div className="hero-background">
            <div className="gradient-overlay"></div>
            <div className="pattern-overlay"></div>
          </div>
          <div className="container">
            <motion.div 
              className="hero-content"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6 }}
            >
              <h1>{t('servicesTitle')}</h1>
              <p>{t('servicesSubtitle')}</p>
            </motion.div>
          </div>
        </section>

        {/* Services Grid Section */}
        <section className="services-grid-section">
          <div className="container">
            <motion.div 
              className="section-header"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6 }}
              viewport={{ once: true }}
            >
              <h2>{t('ourServices')}</h2>
              <p>{t('servicesIntro')}</p>
            </motion.div>
            <motion.div 
              className="services-grid"
              variants={containerVariants}
              initial="hidden"
              whileInView="visible"
              viewport={{ once: true }}
            >
              {services.map((service) => (
                <motion.div
                  key={service.id}
                  className="service-card"
                  variants={itemVariants}
                >
                  <div className="service-header">
                    <div className="service-icon">
                      <i className={`fas fa-${service.icon}`}></i>
                    </div>
                    <h3>{t(service.title)}</h3>
                  </div>
                  
                  <div className="service-image">
                    <img src={service.image} alt={t(service.title)} />
                    <div className="image-overlay">
                      <p>{t(service.description)}</p>
                    </div>
                  </div>

                  <div className="service-content">
                    <div className="service-benefits">
                      <h4>
                        <i className="fas fa-star-of-life"></i>
                        {t('benefits')}
                      </h4>
                      <ul>
                        {service.benefits.map((benefit, idx) => (
                          <li key={idx}>
                            <i className={`fas fa-${benefit.icon}`}></i>
                            <div>
                              <strong>{t(benefit.name)}</strong>
                              <p>{t(benefit.description)}</p>
                            </div>
                          </li>
                        ))}
                      </ul>
                    </div>

                    <div className="service-process">
                      <h4>
                        <i className="fas fa-list-ol"></i>
                        {t('process')}
                      </h4>
                      <div className="process-timeline">
                        {service.process.map((step) => (
                          <div key={step.step} className="process-step">
                            <div className="timeline-line"></div>
                            <div className="step-content">
                              <div className="step-number">{step.step}</div>
                              <div className="step-details">
                                <strong>{t(step.name)}</strong>
                                <p>{t(step.description)}</p>
                              </div>
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>

                    <div className="service-meta">
                      <div className="meta-item duration">
                        <i className="fas fa-clock"></i>
                        <div>
                          <label>{t('duration')}</label>
                          <span>{t(service.duration)}</span>
                        </div>
                      </div>
                      <div className="meta-item warranty">
                        <i className="fas fa-shield-alt"></i>
                        <div>
                          <label>{t('warranty')}</label>
                          <span>{t(service.warranty)}</span>
                        </div>
                      </div>
                    </div>

                    <button className="book-now-btn">
                      <span>{t('bookNow')}</span>
                      <i className="fas fa-arrow-right"></i>
                    </button>
                  </div>
                </motion.div>
              ))}
            </motion.div>
          </div>
        </section>

        {/* Why Choose Us Section */}
        <section className="why-choose-section">
          <div className="container">
            <motion.div
              className="section-header"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6 }}
              viewport={{ once: true }}
            >
              <h2>{t('whyChooseUs')}</h2>
              <p>{t('whyChooseUsSubtitle')}</p>
            </motion.div>

            <motion.div 
              className="features-grid"
              variants={containerVariants}
              initial="hidden"
              whileInView="visible"
              viewport={{ once: true }}
            >
              {features.map((feature, index) => (
                <motion.div
                  key={index}
                  className="feature-card"
                  variants={itemVariants}
                >
                  <div className="feature-icon">
                    <i className={`fas fa-${feature.icon}`}></i>
                  </div>
                  <h3>{t(feature.title)}</h3>
                  <p>{t(feature.description)}</p>
                </motion.div>
              ))}
            </motion.div>
          </div>
        </section>
      </main>

      <Footer />
    </div>
  );
};

export default ServicesPage;
